sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
api.setState("cards_type", event.payload.cards_type);
api.setState("threshold", event.payload.threshold);
api.setState("turn", event.payload.turn);
api.setState("mulligan", event.payload.mulligan);
}
