sys_script_include.script:
var DeckUtil = Class.create();
DeckUtil.prototype = {
    initialize: function() {
    },

calculateAtLeast: function(count, needed){
if (needed === 1) {
    return this.calculateExactPercent(count, 1);
  } else {
    return this.calculateExactPercent(count, needed) + this.calculateAtLeast(count, needed - 1);
  }
},

calculateExactPercent: function(count, needed){
	var successes = needed;
	var neededByTurn = 1;
	var turn = 7 + neededByTurn;
	var probabilityOneTrial = count / 99;
	var coefficient = this.factorial(turn)/(this.factorial(successes) * this.factorial(turn - successes));
	var percentChanceOfSuccess = coefficient * Math.pow(probabilityOneTrial, successes) * Math.pow((1 - probabilityOneTrial), (turn - successes));
	return percentChanceOfSuccess;
},

factorial: function(number){
	if (number === 0) {
    return 1;
  } else {
    return number * this.factorial(number - 1);
  }
},

    type: 'DeckUtil'
};
